# -*- coding: utf-8 -*-from odoo import models, fieldsclass ResPartnerInherit(models.Model):    _inherit = 'res.partner'    # Company Details Tab Fields    company_type = fields.Char(string="Company Type", required=False, )    ownership = fields.Char(string="Ownership", required=False, )    employees_no = fields.Char(string="Number Of Employees", required=False, )    annual_revenue = fields.Char(string="Annual Revenue", required=False, )    assistant_name = fields.Char(string="Assistant Name", required=False, )    assistant_email = fields.Char(string="Assistant Email", required=False, )    assistant_mobile = fields.Char(string="Assistant Mobile", required=False, )    # contact Tab Fields    billing_street = fields.Char(string="Billing Street", required=False, )    shipping_street = fields.Char(string="Shipping Street", required=False, )    billing_city = fields.Char(string="Billing City", required=False, )    shipping_city = fields.Char(string="Shipping City", required=False, )    billing_state = fields.Char(string="Billing State", required=False, )    shipping_state = fields.Char(string="Shipping State", required=False, )    billing_code = fields.Char(string="Billing Code", required=False, )    billing_country = fields.Char(string="Billing Country", required=False, )    shipping_country = fields.Char(string="Shipping Country", required=False, )    convolo_account = fields.Char(string="Convolo Account E-mail", required=False, )    # Quotation Number Of Users    users_no = fields.Integer(string="Users", compute='_compute_quotation_users_number', )    def _compute_quotation_users_number(self):        for rec in self:            rec.users_no = sum(                self.env['sale.order.line'].search([('order_id.partner_id', '=', rec.id)]).mapped('users_no'))    def action_view_quotation_users(self):        self.ensure_one()        return {            'name': _('Number Of Users'),            'type': 'ir.actions.act_window',            'res_model': 'sale.order.line',            'view_mode': 'tree',            'view_id': self.env.ref('contact_crm_sales_custom_fields.view_quotation_users_tree').id,            'domain': [('order_id.partner_id', '=', self.id), ('users_no', '>', 0)],        }